$(document).ready(function () {
    $('select').niceSelect();

    $('#create_new_request_btn').on('click', function (){
        let idPerson = Cookies.get('idProf');
        let idDoctor = $('.listDoctor option:selected').attr('value');
        let day = $('.listDay option:selected').attr('value')
        let time = $('.listTime option:selected').attr('value')

        let info = {
            "idPerson": idPerson,
            "idDoctor": idDoctor,
            "day": day,
            "time": time,
        };

        console.log(JSON.stringify(info));
        $.ajax({
            url: 'http://localhost:3012/create-new-request',         /* Куда пойдет запрос */
            contentType: 'application/json',
            method: 'post',             /* Метод передачи (post или get) */
            dataType: 'html',          /* Тип данных в ответе (xml, json, script, html). */
            data: JSON.stringify(info),     /* Параметры передаваемые в запросе. */
            success: function (data){
                //alert('Вы успешно записались на прием');
                Swal.fire(
                    'Будьте здоровы',
                    'Вы успешно записались на прием!',
                    'success'
                )
                setTimeout(function (){
                    window.location.href = '/my-schedule.html';
                },1500);
            }
        });

    })

    //Удаление записей
    $(document).on('click','.btn-delete-request', function (){
        console.log('delete');
        let idDelete = $(this).parent().parent().attr('newrequest-id');
        let deleteCard = $(this).parent().parent();
        let info = {"id" : idDelete};
        $.ajax({
            url: 'http://localhost:3012/delete-request',         /* Куда пойдет запрос */
            contentType: 'application/json',
            method: 'DELETE',             /* Метод передачи (post или get) */
            dataType: 'html',          /* Тип данных в ответе (xml, json, script, html). */
            data: JSON.stringify(info),     /* Параметры передаваемые в запросе. */
            success: function (data){
                Swal.fire(
                    'Будьте здоровы',
                    'Вы успешно удалили запись на прием!',
                    'success'
                )
                deleteCard.fadeOut(300);
                setTimeout(function (){
                    deleteCard.remove();
                },500);
            }
        });
    });

});


$( document ).ready(function() {
    let name = Cookies.get('name');
    console.log(name);
    $('.lk-dashboard__profile-name').html('Привет, ' + name);


    //Авторизация
    $('#login_btn').on('click', function (){
        var login = $('#login').val();
        var password = $('#password').val();
        let data = {
            "login": login,
            "password": password
        };
        console.log(data);
        $.ajax({
            url: 'http://localhost:3012/login',         /* Куда пойдет запрос */
            contentType: 'application/json',
            method: 'get',             /* Метод передачи (post или get) */
            dataType: 'json',          /* Тип данных в ответе (xml, json, script, html). */
            data: data,     /* Параметры передаваемые в запросе. */
            success: function (data){
                Cookies.set('registration', data.cookies, { expires: 7 })
                Cookies.set('idProf', data.idProf, { expires: 7 })
                Cookies.set('name', data.name, { expires: 7 })
                window.location.href = "http://localhost:3000/lk.html"
            }
        });
    })

    //Выход из аккаунта
    $('#exit_btn').on("click", function (){
        Cookies.remove('registration')
        Cookies.remove('idProf');
        Cookies.remove('name');
        window.location.href = "http://localhost:3000/"
    })

    //Регистрация
    $('#reg_btn').on('click', function (){
        console.log('post');
        let sex = 'Мужской';

        let info = {
            "name": $('#name').val(),
            "lastName": $('#lastName').val(),
            "secondName": $('#secondName').val(),
            "sex": sex,
            "state": $('#state').val(),
            "city": $('#city').val(),
            "address": $('#address').val(),
            "ap_number": $('#ap_number').val(),
            "login": $('#login').val(),
            "password": $('#password').val(),
            "eMail": $('#eMail').val(),
            "phone": $('#phone').val(),
            "bdDate": $('#bdDate').val(),
        };

        console.log(JSON.stringify(info));
       $.ajax({
            url: 'http://localhost:3012/login',         /* Куда пойдет запрос */
            contentType: 'application/json',
            method: 'post',             /* Метод передачи (post или get) */
            dataType: 'json',          /* Тип данных в ответе (xml, json, script, html). */
            data: JSON.stringify(info),     /* Параметры передаваемые в запросе. */
            success: function (data){
                Cookies.set('registration', data.cookies, { expires: 7 })
                Cookies.set('idProf', data.idProf, { expires: 7 })
                Cookies.set('name', data.name, { expires: 7 })
                window.location.href = "http://localhost:3000/registration-success.html"
            }
        });
    })


    $(document).on('click','.show_history_btn',function (){
        Swal.fire(
            'ОРВИ (простуда)',
            'Осмотр. Симптомы: Першение в горле. Боль в горле. Осиплость голоса. Заключение: ОРВИ (простуда)',
            'info'
        )
    })
});
$(document).ready(function () {
    //Регистрация
    $('#update_btn').on('click', function (){
        let info = {
            "id": Cookies.get('idProf'),
            "name": $('#name').val(),
            "lastName": $('#lastName').val(),
            "secondName": $('#secondName').val(),
            "state": $('#state').val(),
            "city": $('#city').val(),
            "address": $('#address').val(),
            "ap_number": $('#ap_number').val(),
            "login": $('#login').val(),
            "password": $('#password').val(),
            "eMail": $('#eMail').val(),
            "phone": $('#phone').val(),
            "bdDate": $('#bdDate').val(),
        };

        console.log(JSON.stringify(info));
        $.ajax({
            url: 'http://localhost:3012/profile',         /* Куда пойдет запрос */
            contentType: 'application/json',
            method: 'PUT',             /* Метод передачи (post или get) */
            dataType: 'json',          /* Тип данных в ответе (xml, json, script, html). */
            data: JSON.stringify(info),     /* Параметры передаваемые в запросе. */
            success: function (data) {
                console.log(data);
                Cookies.set('name', info.name);
                Swal.fire(
                    'Успех',
                    'Вы успешно изменили информацию в профиле',
                    'success'
                )
                setTimeout(function (){
                    window.location.reload();
                },1500);
            }
        });
    })
})